mapper.update:
update association
set member_num = member_num+#{gap,jdbcType=INTEGER}
where id = #{associationId,jdbcType=INTEGER}

update association
set member_num = member_num-#{gap,jdbcType=INTEGER}
where id = #{associationId,jdbcType=INTEGER}

mapper.select:
select COUNT(assoc_name)
FROM association
WHERE adminId=#{adminId};
